import {
    drawBezier
} from './Renderer';
import {
    PrimitiveComponent
} from './PrimitiveComponent';
import {
    Vector
} from 'vectorious';

//TODO: There are multiple optimizations that can be done here, specifically with regard to calculating things only once. the extrema don't necessarily need recomputing just to get the bounding box. the bounding box itself could be stored within the PrimitiveComponent for all types

/**
 * A helper function for finding local extrema given possible solutions
 * @param {object} start a component of the starting coordinate
 * @param {object} c1 a component of the first control point
 * @param {object} c2 a component of the second control point
 * @param {object} end a component of the ending coordinate
 * @param {object} t a possible solution
 */
function _cubicBezier(start, c1, c2, end, t) {
    //uhh... i looked up *SO* much stuff on this, and even tried to work out the math myself,
    //but this is ridiculous - where does this come from?
    return start * (1 - t) * (1 - t) * (1 - t) + 3 * c1 * t * (1 - t) * (1 - t) + 3 * c2 * t * t * (1 - t) + end * t * t * t;
}

/**
 * return the local extremes of the curve
 * @param {object} start a component of the starting vector
 * @param {object} c1 a component of the first control point
 * @param {object} c2 a component of the second control point
 * @param {object} end a component of the ending vector
 */
function _getExtremes(start, c1, c2, end) {
    let a = 3 * end - 9 * c2 + 9 * c1 - 3 * start;
    let b = 6 * c2 - 12 * c1 + 6 * start;
    let c = 3 * c1 - 3 * start;

    let solutions = [];
    let localExtrema = [];

    //"discriminant"
    let disc = b * b - 4 * a * c;

    if (disc >= 0) {
        if (!Math.abs(a) > 0 && Math.abs(b) > 0) {
            solutions.push(-c / b);
        } else if (Math.abs(a) > 0) {
            solutions.push((-b + Math.sqrt(disc)) / (2 * a));
            solutions.push((-b - Math.sqrt(disc)) / (2 * a));
        } else {
            throw new Error("no solutions!?!?!");
        }

        for (let t of solutions) {
            if (0 <= t && t <= 1) {
                localExtrema.push(_cubicBezier(start, c1, c2, end, t));
            }
        }
    }

    localExtrema.push(start, end);

    return localExtrema;
}

/**
 * A Bezier curve
 */
export class Bezier extends PrimitiveComponent {

    /**
     * @param {object} options the options for the bezier curve
     */
    constructor(options) {
        super(options);

        let start = new Vector([options.start.x, options.start.y]);
        let end = new Vector([options.end.x, options.end.y]);
        let control1 = new Vector([options.control1.x, options.control1.y]);
        let control2 = new Vector([options.control2.x, options.control2.y]);

        /**
         * The bounding box of the curve
         */
        this._boundingBox = null;

        /**
         * Helper to update the bounding box
         */
        this._boundingBoxNeedsUpdate = true;

        let xExtrema = _getExtremes(start.x, control1.x, control2, end.x);
        let yExtrema = _getExtremes(start.y, control1.y, control2.y, end.y);
        super.d = new Vector([Math.min.apply(null, xExtrema), Math.min.apply(null, yExtrema)])

        /**
         * The starting point of the curve
         */
        this._start = Vector.subtract(start, this.d);

        /**
         * The ending point of the curve
         */
        this._end = Vector.subtract(end, this.d);

        /**
         * The first control point
         */
        this._control1 = Vector.subtract(control1, this.d);

        /**
         * The second control point
         */
        this._control2 = Vector.subtract(control2, this.d);
    }

    /**
     * get the bounding box of the bezier
     * @type {{top:number, left:number, bottom:number, right:number}} boundingBox
     */
    get boundingBox() {
        //if (this._boundingBox === null || this._boundingBoxNeedsUpdate) {
        let lineWidth = this.style.lineWidth;

        let offset = this.offset;
        let start = Vector.add(this._start, this.offset);
        let control1 = Vector.add(this._control1, this.offset);
        let control2 = Vector.add(this._control2, this.offset);
        let end = Vector.add(this._end, this.offset);

        let xExtrema = _getExtremes(start.x, control1.x, control2, end.x);
        let yExtrema = _getExtremes(start.y, control1.y, control2.y, end.y);
        this._boundingBox = {
            top: Math.min.apply(null, yExtrema) - lineWidth,
            right: Math.max.apply(null, xExtrema) + lineWidth,
            bottom: Math.max.apply(null, yExtrema) + lineWidth,
            left: Math.min.apply(null, xExtrema) - lineWidth
        }
        this._boundingBoxNeedsUpdate = false;
        //}
        return this._boundingBox;
    }

    /**
     * render the bezier curve
     */
    render() {
        drawBezier(
            this._start,
            this._end,
            this._control1,
            this._control2,
            this._prerenderingContext,
            this.style
        );
    }
}

import { drawCircle } from './Renderer';
import { PrimitiveComponent } from './PrimitiveComponent';

/**
 * A circle
 */
export class Circle extends PrimitiveComponent {
    //TODO: provide details about options for docs - link to a separate page
    /**
     * PrimitiveComponent constructor
     * @param {object} options object settings
     */
    constructor(options) {
        super(options)
        /**
         * the radius of the circle
         * @type {number} radius
         */
        this.radius = options.radius || 0;
    }

    /**
     * get the bounding box of the circle;
     * @type {{top:number, left: number, bottom:number, right:number}}
     */
    get boundingBox() {
        //TODO: possibly memory-inefficient - need to research:
        //strokes are (were?) centered over the mathematical perimeter -
        //so half the stroke laid within the perimeter, and the
        //other half laid outside. for some reason, this doesn't
        //work for (0 < lineWidth < 2.0).
        //
        //it's just a pixel, but when a thousand objects are on screen,
        //that'll make a difference
        let offset = this.offset;
        let scale = this.compoundScale;
        return {
            top: offset.y -
                ((this.radius * scale.scaleHeight) +
                    (this.style.lineWidth)),
            left: offset.x -
                ((this.radius * scale.scaleWidth) +
                    (this.style.lineWidth)),
            bottom: offset.y +
                (this.radius * scale.scaleHeight) +
                (this.style.lineWidth),
            right: offset.x +
                (this.radius * scale.scaleWidth) +
                (this.style.lineWidth)
        };
    }

    /**
     * override the render function for drawing circles specifically
     * @override
     */
    render() {
        //the below is to ensure the proper placement when scaling/line widths are accounted for
        let scale = this.compoundScale;
        let lineWidth = this.style.lineWidth;
        drawCircle(
            (this.radius * scale.scaleWidth) + lineWidth,
            (this.radius * scale.scaleHeight) + lineWidth,
            (this.radius * scale.scaleWidth),
            this._prerenderingContext,
            this.style
        );
    }

    /**
     * determine whether the point is in the object
     * basically just the pythagorean theorem
     * @param {number} x the x coordinate
     * @param {number} y the y coordinate
     * @return {boolean} whether or not the point is in the object
     */
    pointIsInObject(x, y) {

        let offset = this.offset;

        //don't bother checking the bounding box because
        //pythagorean formula is closed-form
            let a = x - offset.x;
            let b = y - offset.y;
            let c = this.radius;

            //thanks pythagoras~!
            return (a * a) + (b * b) <= (c * c);
        //use the below when scaling is reimplemented
        /*
		return (
			CanvasObject.prototype.PointIsInObject.call(this, x, y) &&
			Math.pow((x - this.offset.x), 2) / Math.pow((this.radius * this.GlobalScale.scaleWidth), 2) + Math.pow((y - this.offset.y), 2) / Math.pow((this.radius * this.GlobalScale.scaleHeight), 2) <= 1
		);*/
    };
}

import { PrimitiveComponent } from './PrimitiveComponent';

/**
 * The Composition class is an extension of the Primitive that is
 * composed of other extensions of the Primitive. The Composition
 * is used to establish the Scene graph as the parent of all other
 * objects on screen. This is the key abstraction of the [composite
 * pattern](https://en.wikipedia.org/wiki/Composite_pattern): an
 * action taken on the parent element acts upon all of the children,
 * and transatively, all of their children.
 */
export class Composition extends PrimitiveComponent {
    /**
     * @param {object} options object settings
     */
    constructor(options) {
        super(options);
        options = options || {};
        /**
         * The children of this composition
         */
        this._children = options.children || [];
    }

    /**
     * children of this composition
     * @type {Array} children the which compose this object
     */
    get children() {
        return this._children;
    }

    /**
     * the bounding box of the composition (i.e., the containing bounds of all the children of this composition)
     * @type {{top:number, left:number, right:number, bottom:number}} boundingBox
     */
    get boundingBox() {
        let top = Infinity,
            left = Infinity,
            bottom = -Infinity,
            right = -Infinity;

        for (let c of this.children) {
            let boundingBox = c.boundingBox;
            top = Math.min(boundingBox.top, top);
            left = Math.min(boundingBox.left, left);
            bottom = Math.max(boundingBox.bottom, bottom);
            right = Math.max(boundingBox.right, right);
        };

        return {
            top: top,
            left: left,
            bottom: bottom,
            right: right
        };
    }


    /**
     * the an array of children that are found at (x, y)
     * @return {object} childrenAt all the children below the point
     * @param {number} x the x coordinate
     * @param {number} y the y coordinate
     */
    childrenAt(x, y) {
        return this.children.filter((c) => c.PointIsInObject(x, y));
    }

    /**
     * get the top-most child at the (x, y)
     * @return {object} childAt the first child below the point
     * @param {number} x the x coordinate
     * @param {number} y the y coordinate
     */
    childAt(x, y) {
        //loop over the children in reverse because drawing order
        for (var c = this.children.length - 1; c >= 0; c--) {
            if (this.children[c].pointIsInObject(x, y)) {
                return this.children[c];
            }
        }
    }

    /**
     * add a child to this composition
     * @param {object} child the child to be added
     */
    addChild(child) {
        child.parent = this;
        this.children.push(child);
        super.needsRender = true;
        super.needsDraw = true;
        //TODO: make this hook more generic
        //by using a registry
        //if (this.onchildadded) {
        //  this.onchildadded();
        //}
    }

    /**
     * add multiple children to the composition
     * @param {object} children the list of children to be added
     */
    addChildren(children){
        for (let c of children){
            this.addChild(c);
        }
    }

    /**
     * remove a child from this composition
     * @param {object} child the child to be removed
     * @return {object} the child removed
     */
    removeChild(child) {
        if (child) {
            var index = this.children.indexOf(child);
            if (index >= 0) {
                super.needsRender = true;
                super.needsDraw = true;
                return this.children.splice(index, 1);
            }
        }
    };

    /**
     * @override
     * override the render functiont to render the children onto this compositions prerendering canvas
     */
    render() {
        // required to make sure that the drawing occurs within the bounds of this composition
        let boundingBox = this.boundingBox;
        var offset = {
            top: -boundingBox.top,
            left: -boundingBox.left,
            bottom: -boundingBox.bottom,
            right: -boundingBox.right
        };

        for (let c of this.children) {
            c.draw(this._prerenderingContext, offset);
        };

        // `destination-out` will erase things
        //this._prerenderingContext.globalCompositeOperation = 'destination-out';
        //_.each(this.masks, function (m) {
        //m.draw(renderContext, contextOffset);
        //});
        //renderContext.globalCompositeOperation = 'normal';
    };
}

import * as Renderer from './Renderer';
import {
    Composition
} from './Composition';
import {
    PrimitiveComponent
} from './PrimitiveComponent';
import {
    Circle
} from './Circle';
import {
    Ellipse
} from './Ellipse';
import {
    Rectangle
} from './Rectangle';
import {
    Line
} from './Line';
import {
    VectorPath
} from './VectorPath';
import {
    Bezier
} from './Bezier';
import {
    Image
} from './Image';
import {
    Text
} from './Text';

import * as Defaults from './Defaults';

import {
    EventEmitter
} from 'micro-mvc';

import * as Events from './Events';

//const FPS_EPSILON = 10; // +/- 10ms for animation loop to determine if enough time has passed to render
//const DEFAULT_TARGET_FPS = 1000 / 60; //amount of time that must pass before rendering

/**
 * The Compositor class is the entry-point to usage of the `canvas-compositor`.
 * The application programmer is expected to hand over low-level control of the canvas
 * context to the high-level classes and methods exposed by CanvasCompositor.
 *
 * The Compositor class establishes an event dispatcher, animation loop, and scene graph for
 * compositions.
 */
class Compositor extends EventEmitter {
    /**
     * The Compositor class establishes an event dispatcher, animation loop, and scene graph for
     * compositions
     *
     * @param {object} canvas This should be a canvas, either from the DOM or an equivalent API
     *
     * @example
     * let cc = new Compositor(document.getElementById('myCanvas'));
     */
    constructor(canvas) {
        super();
        /**
         * The canvas used by the compositor
         */
        this._canvas = canvas;
        /**
         * The context used by the compositor
         */
        this._context = this._canvas.getContext('2d');

        //acquire the padding on the canvas  this is necessary to properly
        //locate the mouse position
        //TODO: determine if border-box affects this, and adjust accordingly
        let style = window.getComputedStyle(this._canvas);

        /**
         * The x coordinate of the mouse position within the canvas
         * @type {number}
         */
        this._mouseX = null;

        /**
         * The y coordinate of the mouse position within the canvas
         * @type {number}
         */
        this._mouseY = null;

        let borderLeft = style.getPropertyValue('border-left') ? parseFloat(style.getPropertyValue('border-left')) : 0;
        let paddingLeft = style.getPropertyValue('padding-left') ? parseFloat(style.getPropertyValue('padding-left')) : 0;

        /**
         * Any left padding and border added to the canvas must be known to calculate mouse position
         * @type {number}
         */
        this._leftPadding = borderLeft + paddingLeft;

        let borderTop = style.getPropertyValue('border-top') ? parseFloat(style.getPropertyValue('border-top')) : 0;
        let paddingTop = style.getPropertyValue('padding-top') ? parseFloat(style.getPropertyValue('padding-top')) : 0;

        /**
         * Any top padding and border added to the canvas must be known to calculate mouse position
         * @type {number}
         */
        this._topPadding = borderTop + paddingTop;

        /**
         * The timestamp of the current loop of animation
         * @type {number}
         */
        this._currentTime = 0;

        /**
         * The timestamp of the last frame drawn by the animation loop
         * @type {number}
         */
        this._lastFrameTimestamp = 0;

        /**
         * The timestamp of the last render performed by the animation loop
         * @type {number}
         */
        //this._lastRenderTime = 0;

        /**
         * The object last granted "focus"
         * @type {object}
         */
        this._targetObject = null;

        /**
         * The scene composition. This is the root object to be rendered, everything else rendered must be a child of the scene
         * @type {object}
         */
        this._scene = new Composition(this.canvas);

        this._bindEvents();

        this._animationLoop();

        /**
         * The framerate of the animation loop
         * @type {number}
         */
        this._framerate = 0;
    }

    /**
     * retrieve the current framerate
     * @type {number}
     */
    get framerate() {
        //var framerateUpdatedEvent = new Event();
        return this._framerate;
    }

    //TODO: multiple target objects? in reverse order of render? in order of composition?
    /**
     * the object currently selected for interaction
     * @type {object}
     */
    get targetObject() {
        return this._targetObject;
    }
    /**
     * the object currently selected for interaction
     * @param {object} val
     * @type {object}
     */
    set targetObject(val) {
        this._targetObject = val;
    }

    /**
     * the root of the scene graph. add primitives to this to compose an image
     * @type {object}
     */
    get scene() {
        return this._scene;
    }

    /**
     * get the X position of the mouse on the canvas
     * @type {number}
     */
    get mouseX() {
        return this._mouseX;
    }

    /**
     * get the Y position of the mouse on the canvas
     * @type {number}
     */
    get mouseY() {
        return this._mouseY;
    }

    /**
     * The animation loop for this instance of Compositor.
     * Upon receipt of the animation frame from `requestAnimationFrame`, the loop will check
     * whether enough time has passed to redraw for the target framerate.
     * It will only draw if somewhere along the scene graph, an object needs updating.
     * There is no need to invoke this directly, the constructor will do it.
     */
    _animationLoop() {
        window.requestAnimationFrame(this._animationLoop.bind(this));
        this._currentTime = +new Date();
        //set maximum of 60 fps and only redraw if necessary
        if ( /*this._currentTime - this._lastFrameTimestamp >= this._targetFPS &&*/ this.scene.needsDraw) {
            //this._lastRenderTime = +new Date();
            Renderer.clearRect(0, 0, this._canvas.width, this._canvas.height, this._context);
            this.scene.draw(this._context);
        }
        this._framerate = parseInt(1000 / (this._currentTime - this._lastFrameTimestamp));
        this._lastFrameTimestamp = +new Date();

    }

    /**
     * attach interaction events to the canvas. the canvas compositor dispatches
     * events to relevant objects through bridges to the scene graph
     */
    _bindEvents() {
        //must bind to `this` to retain reference

        let _cc = this;
        this._canvas.addEventListener(Events.MOUSEDOWN, (e) => {
            _cc.dispatchEvent(e);
        });

        this._canvas.addEventListener(Events.MOUSEMOVE, (e) => {
            _cc.dispatchEvent(e);
        });
        this._canvas.addEventListener(Events.MOUSEUP, (e) => {
            _cc.dispatchEvent(e);
        });
        this._canvas.addEventListener(Events.MOUSEOUT, (e) => {
            _cc.dispatchEvent(e);
        });
        this._canvas.addEventListener(Events.CLICK, (e) => {
            _cc.dispatchEvent(e);
        });

        this.addEventListener(Events.MOUSEDOWN, this._handleMouseDown);
        this.addEventListener(Events.MOUSEMOVE, this._handleMouseMove);
        this.addEventListener(Events.MOUSEUP, this._handleMouseUp);
        this.addEventListener(Events.MOUSEOUT, this._handleMouseOut);
        this.addEventListener(Events.CLICK, this._handleClick);
    }

    /**
     * bridge the mouse down event on the canvas to the
     * the objects in the scene graph
     */
    _handleMouseDown(e) {
        e.preventDefault();

        let x = e.offsetX - this._leftPadding;
        let y = e.offsetY - this._topPadding;

        //pass through x and y to propagated events
        e.canvasX = x;
        e.canvasY = y;

        let clickedObject = this.scene.childAt(x, y);

        if (clickedObject) {
            clickedObject.dispatchEvent(e);
        }
    }

    /**
     * bridge the mouse up event on the canvas to the
     * the objects in the scene graph
     */
    _handleMouseUp(e) {
        e.preventDefault();

        let x = e.offsetX - this._leftPadding;
        let y = e.offsetY - this._topPadding;

        //pass through x and y to propagated events
        e.canvasX = x;
        e.canvasY = y;

        for (let c of this.scene.children) {
            c.dispatchEvent(e);
        }

        let clickedObject = this.scene.childAt(x, y);

        if (clickedObject) {
            clickedObject.dispatchEvent(e);
        }
    };

    /**
     * bridge the mouse move event on the canvas to the
     * the objects in the scene graph
     */
    _handleMouseMove(e) {
        e.preventDefault();
        this._mouseX = e.offsetX - this._leftPadding;
        this._mouseY = e.offsetY - this._topPadding;

        for (let c of this.scene.children) {
            c.dispatchEvent(e)
        }
    };

    /**
     * bridge the click event on the canvas to the
     * the objects in the scene graph
     */
    _handleClick(e) {
        e.preventDefault();

        let x = e.offsetX - this._leftPadding;
        let y = e.offsetY - this._topPadding;

        //pass through x and y to propagated events
        e.canvasX = x;
        e.canvasY = y;

        let clickedObject = this.scene.childAt(x, y);
        if (clickedObject) {
            clickedObject.dispatchEvent(e);
        }
    };

    /**
     * bridge the mouse out event on the canvas to the
     * the objects in the scene graph
     */
    _handleMouseOut(e) {
        e.preventDefault();

        for (let c of this.scene.children) {
            c.dispatchEvent(e);
        };
    };
}

export {
    Renderer,
    PrimitiveComponent,
    Composition,
    Circle,
    Ellipse,
    Rectangle,
    Line,
    VectorPath,
    Bezier,
    Image,
    Text,
    Defaults,
    Events,
    Compositor
};

/**
 * The default color to fill shapes drawn by the canvas context
 */
export const FILL_STYLE = 'black';

/**
 * The default color to stroke shapes drawn by the canvas context
 */
export const STROKE_STYLE = 'black';

/**
 * The default line cap style for strokes drawn by the canvas. 'round' chosen to match `LINE_JOIN`, which ensures smooth meshing of vertices
 */
export const LINE_CAP = 'round';

/**
 * The default stroke width for shapes drawn by the canvas
 */
export const LINE_WIDTH = 1.0;

/**
 * The default line join style strokes drawn by the canvas. 'round' chosen for smooth meshing of vertices
 */
export const LINE_JOIN = 'round';

/**
 * The default miter limit ratio for mitered line joins
 */
export const MITER_LIMIT = 10;

/**
 * The default font for text drawn by the canvas context
 */
export const FONT = '10px sans-serif';

/**
 * The default text alignment for text drawn by the canvas context
 */
export const TEXT_ALIGN = 'start';

/**
 * The default text baseline for text drawn by the canvas context
 */
export const TEXT_BASELINE = 'alphabetic';

import { drawEllipse } from './Renderer';
import { PrimitiveComponent } from './PrimitiveComponent';

/**
 * An ellipse
 */
export class Ellipse extends PrimitiveComponent {
    /**
     * @param {object} options options for the ellipse
     * @param {number} options.radius the major (horizontal) radius of the ellipse
     * @param {number} options.minorRadius the minor (vertical) radius of the ellipse
     */
    constructor(options) {
        super(options);
        /**
         * @type {number} radius the major radius (horizontal) of the ellipse
         */
        this.radius = options.radius || 0;
        /**
         * @type {number} minorRadius the minor radius (vertical) of the ellipse
         */
        this.minorRadius = options.minorRadius || this.radius || 0;
    }

    /**
     * the bounding box for the ellipse
     * @type {{top: number, left: number, bottom: number, right: number}} boundingBox
     */
    get boundingBox() {
        let offset = this.offset;
        let scale = this.compoundScale;
        let lineWidth = this.style.lineWidth;
        return {
            top: offset.y -
                ((this.minorRadius * scale.scaleHeight) + lineWidth),
            left: offset.x -
                ((this.radius * scale.scaleWidth) + lineWidth),
            bottom: offset.y +
                (this.minorRadius * scale.scaleHeight) + lineWidth,
            right: offset.x +
                (this.radius * scale.scaleWidth) + lineWidth
        };
    }

    /**
     * override the render function specifically for ellipses
     * @override
     */
    render() {
        let scale = this.compoundScale;
        let lineWidth = this.style.lineWidth;
        //TODO: work out scaling of major/minor radius
        //this doesn't make sense
        drawEllipse(
            (this.radius * scale.scaleWidth) + lineWidth,
            (this.minorRadius * scale.scaleHeight) + lineWidth,
            (this.radius * scale.scaleWidth),
            (this.minorRadius * scale.scaleHeight),
            this._prerenderingContext,
            this.style
        );
    }
    /**
     * determine whether the point is in the object
     * basically just the pythagorean theorem
     * @param {number} x the x coordinate
     * @param {number} y the y coordinate
     * @return {boolean} whether or not the point is in the object
     */
    pointIsInObject(x, y) {
        let scale = this.compoundScale;
        let offset = this.offset;

        let a = x - offset.x;
        let b = y - offset.y;

        let c1 = this.radius * scale.scaleWidth;
        let c2 = this.minorRadius * scale.scaleHeight;

        //see: http://math.stackexchange.com/questions/76457/check-if-a-point-is-within-an-ellipse
