    disableDragging() {
        //TODO: should probably be using an event registry so
        //multiple event callbacks can be registered
        this.onmousedown = null;
        this.onmousemove = null;
        this.onmouseup = null;
        this.onmouseout = null;
        this.needsDraw = true;
