        Object.assign(context, style);
        context.beginPath();
        context.moveTo(vertices[0].x, vertices[0].y);
        for (let v = 1; v < vertices.length; v++) {
            context.lineTo(vertices[v].x, vertices[v].y);
        }
        context.stroke();
    }

